mapper.update:
UPDATE gwjc_moni_data set
     data_value = #{data_value},
     data_time = #{data_time}
where conf_Id = #{conf_id}

mapper.delete:
DELETE FROM gwjc_moni_data_history  
where conf_id=#{conf_id}
AND data_time > #{data_time}

mapper.select:
        select id,conf_id,data_value,data_time,input_time,alerm_flag   from  gwjc_moni_data_history t
        where 1=1
		AND conf_id = #{conf_id}
		AND data_time > #{start_date}
		 AND data_time < #{end_date}
		order by data_time
